'use strict';

var inspectObject = require('../../inspectObject');
var extract = require('../../extract');

var struct = require('struct');

function patchConfig(id, obj, callback) {
	var client = this;
	var err = inspectObject(obj, struct.config);
	if (err) return callback(err);
	obj = extract(obj, struct.config);
	client.patchObject('config', id, obj, callback);
}

module.exports = patchConfig;
